import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

const App = () => {
  const [texts, setText] = useState([
    { id:1 , name:"롤 하기", content: "롤 존나 열심히 하기" },
    { id:2 , name:"잠자기", content: "잠 존나 열심히 자기" }
  ]);

    const [name, setName] = useState('');
    const [content, setContent] = useState('');

    const nameChangeHandler = (event) => {
      setName(event.target.value);
    }

    const contentChangeHandler = (event) => {
      setContent(event.target.value);
    }

    // 추가 버튼 클릭
    const clickAddButtonHandler = () => {
      const newText = {
        id: text.length + 1,
        name: name,
        content: content,
      };
      setTexts([...texts, newText])
    };

    // 삭제 버튼 클릭
    const clickRemoveButtonHandler = (id) => {
      const newTexts = texts.filter((user) => text.id !== id);
      setTexts(newTexts);
    };




  return (
    <div className='main'>
      <div className='Navbar'>
        <div>My Todo List</div>
        <div>React</div>
      </div>
      <form className='Formbar'>
        <div>
          <label className='Formbar_name1'>제목</label>
          <input value={name} onChange={nameChangeHandler} className='Formbar_input1'/>
          <label className='Formbar_name2'>내용</label>
          <input value={content} onChange={contentChangeHandler} className='Formbar_input2'/>
        </div>
        <button className='Formbar_button'>추가하기</button>
      </form>
      <div className='content'>
        <h2>끝내고 놀자 세준아..</h2>
        <div className='content_list'>
          <div className='content_box'>
            <div>
              <h2 className='content_name'>롤 하기</h2>
              <div>롤 존나 열심히하기</div>
            </div>
            <div className='content_btn'>
              <button className='delete_btn'>삭제하기</button>
              <button className='complete_btn'>완료</button>
            </div>
          </div>
        </div>
        <h2>끝냈다~!@@!!~!!</h2>
        <div className='content_list'>
          <div className='content_box'>
            <div>
              <h2 className='content_name'>리액트 제출</h2>
              <div>리액트 과제 제출하기</div>
            </div>
            <div className='content_btn'>
              <button className='delete_btn'>삭제하기</button>
              <button className='complete_btn'>취소</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
